Event.Effect:
local set = getScriptObjectFlag(trigger.used_object,'open')
setScriptObjectFlag(trigger.used_object,'open',not set)
local pos = get(trigger.used_object,"position")
if (set) then
	pos[2] = pos[2] +4;
else
	pos[2] = pos[2] -4;
end;
moveObject(trigger.used_object,pos,false)

local set = getScriptObjectFlag(trigger.used_object,'on')
setScriptObjectFlag(trigger.used_object,'on',not set)

insertTrigger("object_use");
local doorid = scriptobjectvar[trigger._id]["door"];
addTriggerVariable("_id",doorid);
addTriggerVariable("used_object",doorid);

if (scriptobjectvar[trigger.used_object]["locked"] ~= true or trigger.lock_override) then
	local set = getScriptObjectFlag(trigger.used_object,'open')
	setScriptObjectFlag(trigger.used_object,'open',not set)

	local pos = get(trigger.used_object,"position")
	local shift={0,4};
	if (not set) then
		shift[2] = -4;
	end

	vecrotate(shift,get(trigger.used_object,"angle"));
	vecadd(pos,shift);

	moveObject(trigger.used_object,pos,false)
end

if (scriptobjectvar[trigger.used_object]["locked"] ~= true or trigger.lock_override) then
	local set = getScriptObjectFlag(trigger.used_object,'open')
	setScriptObjectFlag(trigger.used_object,'open',not set)

	local pos = get(trigger.used_object,"position")
	local shift={-0.7,4};
	if (not set) then
		shift[1] = 0.7;
		shift[2] = -4;
	end

	vecrotate(shift,get(trigger.used_object,"angle"));
	vecadd(pos,shift);

	moveObject(trigger.used_object,pos,false)
end;

if (scriptobjectvar[trigger.used_object]["locked"] ~= true or trigger.lock_override) then
	local set = getScriptObjectFlag(trigger.used_object,'open')
	setScriptObjectFlag(trigger.used_object,'open',not set)

	local pos = get(trigger.used_object,"position")
	local shift={-0.7,-4};
	if (not set) then
	shift[1] = 0.7;
	shift[2] = 4;
	end

	vecrotate(shift,get(trigger.used_object,"angle"));
	vecadd(pos,shift);

	moveObject(trigger.used_object,pos,false)
end;

if (scriptobjectvar[trigger.used_object]["locked"] ~= true or trigger.lock_override) then
	local stat = getScriptObjectFlag(trigger.used_object,'open')
	setScriptObjectFlag(trigger.used_object,'open',not stat)

	local pos = get(trigger.used_object,"position")
	local shift={-0.8,1.65};
	local angRot = get(trigger.used_object,"angle");
	local ang = angRot
	if (not stat) then
		ang = ang - 130;
		shift[1] = 0.8;
		shift[2] = -1.65;
	else
		ang = ang + 130;
		angRot = angRot + 130;
	end;

	vecrotate(shift,angRot);
	vecadd(pos,shift);
	set(trigger.used_object,"angle",ang);


	moveObject(trigger.used_object,pos,false)
end
