Quest.Description:
if (besieged_farm.finished) then
	return _("Quest finished")
else
	return _("Quest description")
end

Topic.Effect:
unitSpeak("LvR",_("Bei Marek!"),1000);
unitSpeak("PC",_("Wer seid ihr?"),1000);
unitSpeak("SL",_("Das ist doch..."),1000);

addCameraPosition(0,get(getRole("PL"),"position"),20,40,20);
addCameraPosition(20000,get(getRole("PL"),"position"),220,60,30);

-- PC und LvR reden mit SL, als die Helden dazu kommen. Eine groesser Menge von Soldaten wartet auf ihren Abmarsch.

speak("SL",_("Ich flehe euch an"),1000);

-- PC: dreht sich zu SL, aber LvR ist schneller

speak("LvR",_("Schweigt! Die Sicherheit des"),1000);

jumpTopic("insertion1");

speak("LvR",_("Schweigt! Die Sicherheit des"),1000);
speak("LvR",_("Ausserdem habt ihr sogar"),1000);
speak("SL",_("Aber der werte Magus"),1000);
speak("RF",_("Sehr wohl! Das Verfolgen"),1000);
speak("PC",_("Und ihr habt wirklich"),1000);
speak("RF",_("Es tut mir aufrichtig"),1000);
speak("PC",_("Danke, das reicht."),1000);

-- RF (vor den Kopf gestossen)

speak("WARast",_("Selbstzufriedener Schmarotzer"),1000);
speak("MAGast",_("Ich hoffe nur, das"),1000);
speak("LvR",_("Allein die Anwesenheit"),1000);
speak("SL",_("!"),1000);
speak("LvR",_("Habt ihr nichts"),1000);
speak("ANY",_("Und da sagt man"),1000);

-- SL entfernt sich. PC und LvR wenden sich zu ihren Pferden, als MB aufgeregt in das Dorf gerannt kommt.

addUnitCommand(besieged_farm_tmp.michel_bron,"walk",3000,"locPeasentMichel");

speak("MB",_("Wachen! Wachen! Hilfe!"),1000);
speak("LvR",_("Schaffte den"),1000);
-- Wache1 und Wahe2 (etwas zoegerlich): Jawohl.

-- Die zwei Wachen stuermen vor, sie fuehlen sich nicht ganz wohl in ihrer Haut.

speak("PC",_("Wartet! Was ist passiert?"),1000);

-- MB wirft sich nieder

speak("MB",_("Vergebt mir, mein Prinz"),1000);
speak("PC",_("Wir..."),1000);
speak("LvR",_("Mein Prinz! Wir"),1000);
speak("PC",_("Ich weiss!"),1000);

-- PC greift in seine Satteltasche, zieht ein Saeckchen hervor und haelt es hoch.

speak("PC",_("200 Goldstuecke jedem,"),1000);
speak("LvR",_("tz"),1000);
speak("ANY",_("200 fuer jeden."),1000);

-- Der Kommunikationskristall erscheint und spricht zu den Helden

speak("SW",_("Denkt nicht einmal daran!"),1000);

addQuestion(_("Dem Bauern helfen?"));
addAnswer(_("Yes"),"help_peasant");
addAnswer(_("No"),"dont_help_peasant");

if (getPlayers[2] == nil) then
	speak("WAR",_("ICH Nur ein Stein"),1000);
else
	speak("WAR",_("WIR Nur ein Stein"),1000);
end;
speak("ANYplus",_("Und es gibt Geld"),1000);
if (getPlayers[2] == nil) then
	speak("PL",_("ICH Dann ist es beschlossen."),1000);
else
	speak("PL",_("WIR Dann ist es beschlossen."),1000);
end;
if (getPlayers[2] == nil) then
	speak("SW",_("Was glaubt ihr, was ihr da tut?"),1000);
else
	speak("SW",_("Was glaubt ihr, was ihr da tut?"),1000);
end;
if (getPlayers[2] == nil) then
	speak("PL",_("ICH Wann"),1000);
else
	speak("PL",_("WIR Wann"),1000);
end;
speak("MAGast",_("Ja, zeig ihm,"),1000);
if (getPlayers[2] == nil) then
	speak("PL",_("ICH Prinz,"),1000);
else
	speak("PL",_("WIR Prinz,"),1000);
end;

jumpTopic("insertion2");

if (getPlayers[2] == nil) then
	speak("PC",_("ICH"),1000);
else
	speak("PC",_("WIR"),1000);
end;
speak("WARast",_("unter der Knute eines gewissen"),1000);
speak("SW",_("Windklaue! Du"),1000);
speak("WARast",_("ups..."),1000);
speak("PL",_("Ist das ein Problem"),1000);
speak("PC",_("... wahrscheinlich nicht."),1000);
speak("LvR",_("Seid ihr sicher,"),1000);
if (getPlayers[2] == nil) then
	speak("PC",_("ICH"),1000);
else
	speak("PC",_("WIR"),1000);
end;

-- PC wirft SL das Saeckchen zu, der es faengt.

speak("SL",_("Sehr wohl mein Prinz."),1000);

-- => Quest: „Der belagerte Bauernhof“

besieged_farm.started = true;

changeTopic("end_cutscene");

speak("PC",_("Niemand? Sergeant!"),1000);
speak("SL",_("Mein Prinz?"),1000);
speak("PC",_("Mein Wort steht."),1000);
speak("SL",_("Wie ihr es wuenscht,"),1000);
speak("PC",_("Es tut mir Leid,"),1000);

-- LvR (schuettelt hinter dem Ruecken des Prinzen nur den Kopf): MB bricht zusammen

changeTopic("end_cutscene");

Event.Condition:
if (scene2.scene2 == true and besieged_farm.scene3 ~= true) then
	return (unitIsInArea(trigger.player,"PrinceArea"));
else
	return false;
end;

Event.Effect:
besieged_farm_tmp.michel_bron = createObject("peasant", "entry_west");
setRefName(besieged_farm_tmp.michel_bron, "Michel Bron");
besieged_farm_tmp.soren_windclaw_crystal = createObject("peasant", "locSummoner");
setRefName(besieged_farm_tmp.soren_windclaw_crystal, "Soren Windclaw");
createDialogue();
addSpeaker(prince_charming,"PC");
addSpeaker(leonard_von_robenforst,"LvR");
addSpeaker(sergeant_lutterer,"SL");
addSpeaker(magus_convocatis_relau_feuerweber,"RF");
addSpeaker(besieged_farm_tmp.michel_bron,"MB");
addSpeaker(besieged_farm_tmp.soren_windclaw_crystal,"SW");
addSpeaker(trigger.player,"Player");
addStandardSpeakers();
setCutsceneMode(true);
setTopicBase("Prince Charming");
changeTopic("scene3_dialogue");
