Event.Condition:
if (scene4.scene4 == true and scene5.scene5 ~= true) then
	return (unitIsInArea(trigger.player,"aisMounPathEntryEastArea"));
else
	return false;
end;

Event.Effect:
scene5_tmp.guard1 = createObject("peasant", "entry_east");
setRefName(scene5_tmp.guard1, "Guard");

scene5_tmp.guard2 = createObject("peasant", "entry_east");
setRefName(scene5_tmp.guard2, "Guard");
setObjectValue(scene5_tmp.guard2,"max_health",10000);
setObjectValue(scene5_tmp.guard2,"health",10000);

scene5_tmp.erzmaga_ireana = createObject("peasant", "entry_east");
setRefName(scene5_tmp.erzmaga_ireana, "Erzmaga Ireana von Nordenburg");
