setObjectValue(scene5_tmp.erzmaga_ireana,"health",10000);

scene5_tmp.hauptmann_der_wache = createObject("peasant", "entry_east");
setRefName(scene5_tmp.hauptmann_der_wache, "Hauptmann der Wache");
setObjectValue(scene5_tmp.hauptmann_der_wache,"max_health",10000);
setObjectValue(scene5_tmp.hauptmann_der_wache,"health",10000);

scene5_tmp.elementaristenwaechter = createObject("peasant", "entry_east");
setRefName(scene5_tmp.elementaristenwaechter, "Elementaristenwaechter");
setObjectValue(scene5_tmp.elementaristenwaechter,"max_health",10000);
setObjectValue(scene5_tmp.elementaristenwaechter,"health",10000);

createDialogue();
