Event.Condition:
if (ambush.started == true and theFissure.rescuedIerana ~= true) then
	return (unitIsInArea(trigger.player,"areaHelpIerana"));
else
	return false;
end;

Event.Effect:
theFissure_tmp.skeletons={};
for nr=1,8 do
	theFissure_tmp.skeletons[nr] = createObject("fire_skel","locSkeleton"..nr);
end;
theFissure_tmp.skeletons_nr =9;

setCutsceneMode(true);

timedExecute('addUnitCommand(theFissure_tmp.skeletons[1], "fire_shot", theFissure_tmp.guard2,0,"repeat")',400);
timedExecute('addUnitCommand(theFissure_tmp.skeletons[2], "fire_shot", theFissure_tmp.guard2,0,"repeat")',350);
timedExecute('addUnitCommand(theFissure_tmp.skeletons[3], "fire_shot", theFissure_tmp.guard4,0,"repeat")',100);
timedExecute('addUnitCommand(theFissure_tmp.skeletons[4], "fire_shot", theFissure_tmp.hauptmann_der_wache,0,"repeat")',75);
timedExecute('addUnitCommand(theFissure_tmp.skeletons[5], "fire_shot", theFissure_tmp.ierana,0,"repeat")', 800);
timedExecute('addUnitCommand(theFissure_tmp.skeletons[6], "fire_shot", theFissure_tmp.guard1,0,"repeat")', 650);
timedExecute('addUnitCommand(theFissure_tmp.skeletons[7], "fire_shot", theFissure_tmp.guard1,0,"repeat")',523);
timedExecute('addUnitCommand(theFissure_tmp.skeletons[8], "fire_shot", theFissure_tmp.guard3,0,"repeat")', 250);

lookAtObject(theFissure_tmp.guard1, theFissure_tmp.skeletons[6]);
lookAtObject(theFissure_tmp.guard2, theFissure_tmp.skeletons[2]);
lookAtObject(theFissure_tmp.guard3, theFissure_tmp.skeletons[8]);
lookAtObject(theFissure_tmp.guard4, theFissure_tmp.skeletons[3]);
lookAtObject(theFissure_tmp.ierana, theFissure_tmp.hauptmann_der_wache);
lookAtObject(theFissure_tmp.hauptmann_der_wache, theFissure_tmp.ierana);

addCameraPosition(0,get(getRole("PL"),"position"),-50,40,20);
addCameraPosition(5000,get(theFissure_tmp.ierana,"position"),-50,40,20);
--TODO timer wieder auf 10000
startTimer('theFissure_dialogue1',1000);
print("attacking the convoi")

addCameraPosition(1000,get(getRole("PL"),"position"),-50,40,20);
-- Die Party erreicht den Ort des Geschehens. Die Kutsche der Erzmaga
-- ist verunglueckt, mehrere Skelettbogenschuetzen beschießen sie mit
-- Feuerpfeilen und die Wachen sind allesamt verwundet.

playersLookAt(getPosition(theFissure_tmp.ierana));
