Topic.Effect:
setCutsceneMode(false);

for nr=1,8 do
	clearUnitCommands(theFissure_tmp.skeletons[nr]);
end;
startTimer("undeadReinforcements",5000);
set(theFissure_tmp.hauptmann_der_wache,"state","inactive");

Event.Effect:
local i,skel;
for i=1,2 do
	skel = createObject("sword_skel","entry_north");
	addUnitCommand(skel,"attack",theFissure_tmp.ierana,nil,"secondary");
	theFissure_tmp.skeletons_nr = theFissure_tmp.skeletons_nr + 1;
end;
for i=1,2 do
	skel = createObject("axe_skel","entry_north");
	addUnitCommand(skel,"attack",theFissure_tmp.ierana,nil,"secondary");
	theFissure_tmp.skeletons_nr = theFissure_tmp.skeletons_nr + 1;
end;
for i=1,2 do
	skel = createObject("bow_skel","entry_north");
	addUnitCommand(skel,"attack",theFissure_tmp.ierana,nil,"secondary");
	theFissure_tmp.skeletons_nr = theFissure_tmp.skeletons_nr + 1;
end;
startTimer("undeadMonster", 4000);

Event.Condition:
-- caught subtypes: bow_skel, sword_skel, axe_skel, fire_skel, reaper_skel
if((get(trigger.unit,'subtype') == 'bow_skel') or (get(trigger.unit,'subtype') == 'sword_skel') or (get(trigger.unit,'subtype') == 'axe_skel') or (get(trigger.unit,'subtype') == 'fire_skel') or (get(trigger.unit,'subtype') == 'reaper_skel'))then
	return true;
end;
return false;

Event.Effect:
theFissure_tmp.skeletons_nr = theFissure_tmp.skeletons_nr -1;
if (theFissure_tmp.skeletons_nr == 0) then

	if (isCreature(theFissure_tmp.guard2)) then
		addUnitCommand(theFissure_tmp.guard2, "walk", "locGuard2")
	end;
	if (isCreature(theFissure_tmp.guard3)) then
		addUnitCommand(theFissure_tmp.guard3, "walk", "locGuard3")
	end;
	if (isCreature(theFissure_tmp.guard4)) then
		addUnitCommand(theFissure_tmp.guard4, "walk", "locGuard4")
	end;

	startTimer("theFissure_dialogue2",3000);
end;

--gespraech nach dem Kampf mit den Untoten
set(theFissure_tmp.hauptmann_der_wache,"state","active");

theFissure_tmp.soren_windclaw_crystal = createObject("sorenCrystal", "locDeadSkeleton4");
setRefName(theFissure_tmp.soren_windclaw_crystal, "Soren Windclaw");
setObjectValue(theFissure_tmp.soren_windclaw_crystal,"max_health",10000);
setObjectValue(theFissure_tmp.soren_windclaw_crystal,"health",10000);



createDialogue();
addSpeaker(theFissure_tmp.soren_windclaw_crystal,"SW");
